  ,description       VARCHAR(206)
  ,txt               VARCHAR(4020) NOT NULL
  ,price             VARCHAR(12) NOT NULL
  ,discount5         VARCHAR(12) NOT NULL
  ,discount15        VARCHAR(10) NOT NULL
  ,discount30        VARCHAR(10) NOT NULL
  ,url               VARCHAR(9) NOT NULL
  ,content           VARCHAR(3591)
  ,howToUse          VARCHAR(918)
  ,storage           VARCHAR(123)
